/**
 * Filter a chat history to only the last 2 hours and convert dates to a given time zone.
 *
 * @param {Array<{date:string,message:string,role:string}>} history
 * @param {string} tz IANA time zone, e.g. "America/New_York"
 * @param {number} hoursBack (optional) default 2
 * @returns {Array<{date:string,message:string,role:string}>}
 */
function filterAndConvert(history, tz, hoursBack = 2) {
  const nowUtc = Date.now();
  const cutoffUtc = nowUtc - hoursBack * 60 * 60 * 1000;

  return history
    .filter(item => new Date(item.date).getTime() >= cutoffUtc)
    .map(item => ({
      ...item,
      date: toTZISO(item.date, tz),
    }));
}

/**
 * Convert an ISO string to the same moment expressed in another time zone,
 * returned as "YYYY-MM-DDTHH:mm:ss[±HH:MM]" (no milliseconds).
 *
 * Uses only built-ins (Intl.DateTimeFormat).
 */
function toTZISO(dateString, tz) {
  const d = new Date(dateString);

  // Grab all parts in the desired TZ
  const parts = new Intl.DateTimeFormat('en-CA', {
    timeZone: tz,
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: false,
  }).formatToParts(d).reduce((acc, p) => {
    acc[p.type] = p.value;
    return acc;
  }, {});

  // Build local datetime string
  const local = `${parts.year}-${parts.month}-${parts.day}T${parts.hour}:${parts.minute}:${parts.second}`;

  // Compute offset for that TZ at that instant
  const offsetMinutes = getOffsetMinutes(d, tz);
  const sign = offsetMinutes >= 0 ? '+' : '-';
  const abs = Math.abs(offsetMinutes);
  const hh = String(Math.floor(abs / 60)).padStart(2, '0');
  const mm = String(abs % 60).padStart(2, '0');

  return `${local}${sign}${hh}:${mm}`;
}

/**
 * Find the UTC offset (in minutes) of a date in a given TZ using Intl.
 */
function getOffsetMinutes(date, tz) {
  // Format the same date in UTC and in the TZ, compare epoch millis reconstructed
  const fmt = (zone) => {
    const parts = new Intl.DateTimeFormat('en-CA', {
      timeZone: zone,
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: false,
    }).formatToParts(date).reduce((acc, p) => {
      acc[p.type] = p.value;
      return acc;
    }, {});
    return Date.UTC(
      Number(parts.year),
      Number(parts.month) - 1,
      Number(parts.day),
      Number(parts.hour),
      Number(parts.minute),
      Number(parts.second)
    );
  };

  const asUtcMillis = fmt('UTC');
  const asTzMillis = fmt(tz);
  return (asTzMillis - asUtcMillis) / (60 * 1000);
}

const history = $('Get Chat History').first().json.chat_history;
const tz = $('Get Time Zone').first().json.timeZone || 'America/New_York';

const filtered = filterAndConvert(history, tz);

// Return as n8n items
return { json: { history: filtered } };
