const getDefaultStartDate = (timeZone) => {
  const now = DateTime.now().setZone(timeZone);

  let resultDate;

  if (now.hour < 19) {
    // Add 1 hour, round down to the top of the next hour (00 mins, 00 secs)
    resultDate = now.plus({ hours: 1 }).set({ minute: 0, second: 0, millisecond: 0 });
  } else {
    // After or at 7 PM → Tomorrow at 07:00:00
    resultDate = now.plus({ days: 1 }).set({ hour: 7, minute: 0, second: 0, millisecond: 0 });
  }

  // Return ISO string with timezone offset, no milliseconds
  return resultDate.toISO({ suppressMilliseconds: true });
};

// Example usage
const timeZone = $('When Executed by Another Workflow').first().json.user.time_zone; // Replace or get dynamically
const result = getDefaultStartDate(timeZone);

return [{ json: { startDate: result } }];

const getDefaultEndDate = (timeZone) => {
  const now = DateTime.now().setZone(timeZone);

  let targetDate;
  if (now.hour < 19) {
    targetDate = now.set({ hour: 20, minute: 0, second: 0, millisecond: 0 });
  } else {
    targetDate = now.plus({ days: 1 }).set({ hour: 20, minute: 0, second: 0, millisecond: 0 });
  }

  return targetDate.toISO({ suppressMilliseconds: true });
};

const timeZone = $('When Executed by Another Workflow').first().json.user.time_zone;
const result = getDefaultEndDate(timeZone);

return [{ json: { endDate: result } }];

// Inputs
const startDate = $('Consolidate Dates').first().json.startDate;
const endDate = $('Consolidate Dates').first().json.endDate;
const timeZone = $('When Executed by Another Workflow').first().json.user.time_zone || 'UTC';

// Parse to UTC
const startUTC = DateTime.fromISO(startDate).toUTC();
const endUTC = DateTime.fromISO(endDate).toUTC();

const generateNighttimeBlocks = (start, end) => {
	const blocks = [];
	let current = start.startOf('day');

	while (current <= end) {
		const nightStart = current.set({ hour: 20, minute: 0, second: 0, millisecond: 0 });
		const nightEnd = current.set({ hour: 23, minute: 59, second: 59, millisecond: 0 });

		if (nightStart >= start && nightStart <= end) {
			blocks.push({
				start: nightStart.setZone(timeZone).toISO({ suppressMilliseconds: true }),
				end: nightEnd.setZone(timeZone).toISO({ suppressMilliseconds: true }),
			});
		}

		const nextMorningStart = current.plus({ days: 1 }).set({ hour: 0 });
		const nextMorningEnd = nextMorningStart.set({ hour: 6 });

		if (nextMorningEnd >= start && nextMorningStart <= end) {
			blocks.push({
				start: nextMorningStart.setZone(timeZone).toISO({ suppressMilliseconds: true }),
				end: nextMorningEnd.setZone(timeZone).toISO({ suppressMilliseconds: true }),
			});
		}

		current = current.plus({ days: 1 });
	}

	return blocks;
};

const generateWeekendBlocks = (start, end) => {
	const blocks = [];
	let current = start.startOf('day');
	let weekendStart = null;

	while (current <= end) {
		const isWeekend = current.weekday === 6 || current.weekday === 7;

		if (isWeekend && !weekendStart) {
			weekendStart = current > start ? current : start;
		}

		if (!isWeekend && weekendStart) {
			const weekendEnd = current < end ? current : end;
			if (weekendEnd > weekendStart) {
				blocks.push({
					start: weekendStart.setZone(timeZone).toISO({ suppressMilliseconds: true }),
					end: weekendEnd.setZone(timeZone).toISO({ suppressMilliseconds: true }),
				});
			}
			weekendStart = null;
		}

		current = current.plus({ days: 1 });
	}

	if (weekendStart && end > weekendStart) {
		blocks.push({
			start: weekendStart.setZone(timeZone).toISO({ suppressMilliseconds: true }),
			end: end.setZone(timeZone).toISO({ suppressMilliseconds: true }),
		});
	}

	return blocks;
};

const nighttimeBlocks = generateNighttimeBlocks(startUTC, endUTC);
const weekendBlocks = generateWeekendBlocks(startUTC, endUTC);
const busy = $input.first().json.calendars.primary.busy.concat(weekendBlocks);

return [{
	json: {
		nighttimeBlocks,
		weekendBlocks,
        busy: $('When Executed by Another Workflow').first().json.task.category.toLowerCase() === 'event' ?  busy.concat(nighttimeBlocks) : busy
	}
}];

const startStr = $('Consolidate Dates').first().json.startDate;
const endStr = $('Consolidate Dates').first().json.endDate;
const incrementMinutes = $('Consolidate Dates').first().json.duration ?? 60;
const timeZone = $('When Executed by Another Workflow').first().json.user.time_zone || 'UTC';

if (incrementMinutes <= 0) {
	return [{ json: { intervals: [] } }];
}

// Parse input in any zone, convert to UTC for logic
const startUTC = DateTime.fromISO(startStr).toUTC();
const endUTC = DateTime.fromISO(endStr).toUTC();

if (startUTC > endUTC) {
	return [{ json: { intervals: [] } }];
}

// Generate timestamps in UTC
const timestamps = [];
let current = startUTC;

while (current <= endUTC) {
	timestamps.push(current);
	current = current.plus({ minutes: incrementMinutes });
}

// Pair timestamps, convert to output TZ
const intervals = [];

for (let i = 0; i < timestamps.length - 1; i++) {
	intervals.push({
		start: timestamps[i].setZone(timeZone).toISO({ suppressMilliseconds: true }),
		end: timestamps[i + 1].setZone(timeZone).toISO({ suppressMilliseconds: true }),
	});
}

return [{ json: { intervals } }];

const timeZone = $('When Executed by Another Workflow').first().json.user.time_zone || 'UTC';
const available = $input.first().json.intervals || [];
const busy = $('Generate Busy Blocks').first().json.busy || [];

// --- Normalize and merge busy intervals in UTC ---
const mergeIntervals = (intervals) => {
	if (!intervals.length) return [];

	const sorted = intervals
		.map(i => ({
			start: DateTime.fromISO(i.start).toUTC(),
			end: DateTime.fromISO(i.end).toUTC(),
		}))
		.sort((a, b) => a.start - b.start);

	const merged = [sorted[0]];

	for (let i = 1; i < sorted.length; i++) {
		const last = merged[merged.length - 1];
		const current = sorted[i];

		if (current.start <= last.end) {
			last.end = current.end > last.end ? current.end : last.end;
		} else {
			merged.push(current);
		}
	}

	return merged;
};

// --- Subtract merged busy intervals from available ---
const removeConflicts = (available, busyMerged) => {
	const result = [];

	for (const a of available) {
		const aStartUTC = DateTime.fromISO(a.start).toUTC();
		const aEndUTC = DateTime.fromISO(a.end).toUTC();
		let currentStart = aStartUTC;

		for (const b of busyMerged) {
			if (b.end <= currentStart) continue;
			if (b.start >= aEndUTC) break;

			if (b.start > currentStart) {
				result.push({
					start: currentStart,
					end: b.start,
				});
			}
			currentStart = b.end > currentStart ? b.end : currentStart;
		}

		if (currentStart < aEndUTC) {
			result.push({
				start: currentStart,
				end: aEndUTC,
			});
		}
	}

	// Filter and convert to target time zone
	return result
		.filter(i => i.end > i.start)
		.map(i => ({
			start: i.start.setZone(timeZone).toISO({ suppressMilliseconds: true }),
			end: i.end.setZone(timeZone).toISO({ suppressMilliseconds: true }),
		}));
};

// Run
const mergedBusy = mergeIntervals(busy);
const finalAvailable = removeConflicts(available, mergedBusy);

// Output
return {json: {finalAvailable}};

const inputSlots =  $input.first().json.data || [];
const timeZone = $('When Executed by Another Workflow').first().json.user.time_zone || 'UTC';

const transformed = inputSlots.map(slot => {
	const start = DateTime.fromISO(slot.start).setZone(timeZone);
	const end = DateTime.fromISO(slot.end).setZone(timeZone);

	const duration = Math.floor(end.diff(start, 'minutes').minutes);

	// Format time: "09:00 AM" → "9:00 AM"
	let displayTime = start.toFormat('hh:mm a');
	if (displayTime.startsWith('0')) {
		displayTime = displayTime.slice(1);
	}

	// Format date: "Mon, May 12"
	const displayDate = start.toFormat("ccc, LLL d");

	// Full: "Mon, May 12 9:00 AM"
	const displayDatetime = `${displayDate} ${displayTime}`;

	return {
		raw_datetime: slot.start,
		duration,
		display_time: displayTime,
		display_date: displayDate,
		display_datetime: displayDatetime
	};
});

return [{ json: { transformed } }];
